<style>

* {
  box-sizing: border-box;
}

.fundo-verde {
  background-color: green;
}
.fundo-amarelo {
  background-color: lightblue;
}
.fundo-azul {
  background-color: #0084C5;
}
.fundo-vermelho {
  background-color: red;
}
.fundo-tomate {
  background-color: tomato;
}
.fundo-tomate {
  background-color: tomato;
}
.fundo-chumbo {
  background-color: #5499C7;
}
.fundo-prata {
  background-color: #E7E7E7;
}
.fundo-#5499C7 {
  background-color: #5499C7;
}

.fundo-vermelho {
  background-color:red;
}
.fundo-mediumskyblue {
  background-color: mediumskyblue;
}

.flex {
  display: -webkit-box;
  display: -moz-box;
  display: -ms-flexbox;
  display: -webkit-flex;
  display: flex;  
  align-items: stretch;
  align-content: stretch;
  justify-content: center;
  flex-wrap: wrap;
  box-sizing: border-box;
  width: 100%;
  max-width: 1024px;
  margin: 0 auto;
}

.flex > div {
  padding: 0;
  box-sizing: border-box;
  position: relative;
}

.flex > div > div {
  position: relative;
  height: 100%;
  box-sizing: border-box;
  padding: 20px;
}

.flex-icones > div {
  padding-top: 70px!important;
  box-sizing: border-box;
  position: relative;
}

.flex img {
  max-width: 100%;
  height: auto!important;
}

.bloco-verde .flex > div > div {
  background-color: green;
}

.bloco-amarelo .flex > div > div {
  background-color: lightblue;
}

.bloco-vermelho .flex > div > div {
  background-color: red;
}

.bloco-chumbo .flex > div > div {background-color: #5499C7;}

.fundo-transparent > div {  background-color: transparent!important;}

.fundo-Branco {
  background-color: white;
}
.fundo-azul-claro {
  background-color: #BDDBD6;
}
.texto-centralizado  {text-align: center!important;}

.titulo-centralizado h2 {text-align: center!important;}

.titulo-Branco  {color: white;}
.titulo-Branco h2 {color: white;}
.titulo-vermelho  {color: red;}
.titulo-vermelho h2 {color: red;}
.texto-Branco {color: white;}
.titulo-amarelo h2 {color: lightblue;}
.texto-amarelo {color: lightblue;}
.titulo-chumbo h2 {color: #5499C7;}
